mapper.select:
SELECT B.WCRF_ID , B.WCRF_NM , B.WCRF_DESC , B.WCRF_AMT , B.ARTIST_NM , A.QY AS WCRF_CNT , B.ATCH_FILE_ID , B.RGSTR_ID , B.RGST_DT , B.UPDTR_ID , B.UPDT_DT FROM TB_WCRF_BSKT A, TB_WCRF_INFO B WHERE 0 = 0
AND A.BSKT_ID = #{bsktId}
AND B.WCRF_ID = #{wcrfId}
AND A.MBER_ID = #{mberId}
AND A.WCRF_ID = B.WCRF_ID

mapper.insert:
INSERT INTO TB_WCRF_BSKT (
  BSKT_ID
, WCRF_ID
, MBER_ID
, QY
, RGSTR_ID
, RGST_DT
, UPDTR_ID
, UPDT_DT
) VALUES (
  #{bsktId}
, #{wcrfId}
, #{mberId}
, #{qy}
, #{rgstrId}
, now()
, #{updtrId}
, now()
)

mapper.update:
UPDATE TB_WCRF_BSKT SET
        QY = #{qy}
,       UPDTR_ID = #{updtrId}
,       UPDT_DT = now()
WHERE   BSKT_ID = #{bsktId}

mapper.select:
select A.PURCHS_LST_ID, A.PURCHS_DT, A.PURCHS_ALL_AMT, A.PURCHS_PROGRS_STTUS, GET_CODE_NM('WOD002',A.PURCHS_PROGRS_STTUS) AS PURCHS_PROGRS_STTUS_NM, A.PURCHS_DESC, B.BYER_NM, B.ORER_NM, GET_CODE_NM('WOD001',A.SETLE_SE) AS SETLE_SE_NM, D.SETLE_AMT from TB_WCRF_BUY_TOT A, TB_WCRF_BUY_ADDR B, TB_WCRF_BUY_MST C, TB_WCRF_PAY D where A.PURCHS_LST_ID = B.PURCHS_LST_ID AND A.PURCHS_MST_ID = C.PURCHS_MST_ID AND A.PURCHS_LST_ID = D.PURCHS_LST_ID AND A.PURCHS_PROGRS_STTUS <> '01'
AND A.PURCHS_DESC LIKE CONCAT('%',#{searchKeyword},'%')
AND A.SETLE_SE = #{searchSetleSe}
AND A.PURCHS_PROGRS_STTUS = #{searchPurchsProgrsSttus}
AND B.BYER_NM LIKE CONCAT('%',#{searchByerNm},'%')
AND DATE_FORMAT(A.PURCHS_DT, '%Y/%m/%d') = #{searchPurchsDt}
AND C.MBER_ID = #{rgstrId}
ORDER BY A.RGST_DT DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) from TB_WCRF_BUY_TOT A, TB_WCRF_BUY_ADDR B, TB_WCRF_BUY_MST C, TB_WCRF_PAY D where A.PURCHS_LST_ID = B.PURCHS_LST_ID AND A.PURCHS_MST_ID = C.PURCHS_MST_ID AND A.PURCHS_LST_ID = D.PURCHS_LST_ID AND A.PURCHS_PROGRS_STTUS <> '01'
AND A.PURCHS_DESC LIKE CONCAT('%',#{searchKeyword},'%')
AND A.SETLE_SE = #{searchSetleSe}
AND A.PURCHS_PROGRS_STTUS = #{searchPurchsProgrsSttus}
AND B.BYER_NM LIKE CONCAT('%',#{searchByerNm},'%')
AND DATE_FORMAT(A.PURCHS_DT, '%Y/%m/%d') = #{searchPurchsDt}
AND C.MBER_ID = #{rgstrId}

select A.EXPRN_REQST_ID AS PURCHS_LST_ID, A.REQ_DT AS PURCHS_DT, A.EXPRN_AMT AS PURCHS_ALL_AMT, A.REQ_STTUS AS PURCHS_PROGRS_STTUS, GET_CODE_NM('WOD008',A.REQ_STTUS) AS PURCHS_PROGRS_STTUS_NM, C.EXPRN_NM AS PURCHS_DESC, A.REQ_NM AS BYER_NM, A.REQ_NM AS ORER_NM, GET_CODE_NM('WOD001',B.SETLE_SE) AS SETLE_SE_NM, B.SETLE_AMT, A.EXPRN_NMPR AS QY, GET_CODE_NM('WOD010',C.EXPRN_SE) AS EXPRN_SE_NM, C.BGN_DE, C.END_DE, B.RCPMNY_NM, GET_CODE_NM('WOD009',B.CASH_RECEIPTS_OPT) AS CASH_RECEIPTS_OPT_NM, A.EXPRN_DAY, A.EXPRN_TIME, C.EXPRN_TIME AS EXPRN_TIME2 from TB_EXPRN_REQ A, TB_WCRF_PAY B, TB_EXPRN_INFO C where A.EXPRN_REQST_ID = B.PURCHS_LST_ID AND A.EXPRN_ID = C.EXPRN_ID AND A.REQ_STTUS <> '01'
AND C.EXPRN_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.MBER_ID = #{rgstrId}
AND C.TYPE_CD = #{typeCd}
AND A.REQ_NM LIKE CONCAT('%',#{searchByerNm},'%')
AND B.SETLE_SE = #{searchSetleSe}
AND A.EXPRN_DAY = REPLACE(#{searchPurchsDt}, '-', '')
AND DATE_FORMAT(A.REQ_DT, '%Y-%m-%d') >= #{searchBgnDe}
AND DATE_FORMAT(A.REQ_DT, '%Y-%m-%d') <= #{searchEndDe}
AND A.REQ_STTUS = #{searchPurchsProgrsSttus}
AND C.EXPRN_NM LIKE CONCAT('%',#{searchPurchsDesc},'%')
ORDER BY A.REGIST_DT DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) from
TB_EXPRN_REQ A,
TB_WCRF_PAY B,
TB_EXPRN_INFO C
where
A.EXPRN_REQST_ID = B.PURCHS_LST_ID
AND A.EXPRN_ID = C.EXPRN_ID
AND A.REQ_STTUS  <> '01' 

    AND C.EXPRN_NM LIKE CONCAT('%',#{searchKeyword},'%')


    AND A.MBER_ID = #{rgstrId}


    AND C.TYPE_CD = #{typeCd}


    AND A.REQ_NM LIKE CONCAT('%',#{searchByerNm},'%')


    AND B.SETLE_SE = #{searchSetleSe}


    AND A.EXPRN_DAY = REPLACE(#{searchPurchsDt}, '-', '')


     AND DATE_FORMAT(A.REQ_DT, '%Y-%m-%d') >= #{searchBgnDe} 


     AND DATE_FORMAT(A.REQ_DT, '%Y-%m-%d') <= #{searchEndDe} 


    AND A.REQ_STTUS = #{searchPurchsProgrsSttus}


    AND C.EXPRN_NM LIKE CONCAT('%',#{searchPurchsDesc},'%')
