mapper.select:
select B.PURCHS_LST_ID, B.SETLE_SE, B.PURCHS_ALL_AMT, B.PURCHS_DT, C.ORER_ADDR, C.ORER_ADDR_DTL, C.BYER_NM, A.SETLE_AMT, A.SETLE_DT, C.ORER_NM, C.ORER_TEL_NO, C.ORER_HP, C.ORER_ZIP, C.ORDER_MESS_CN, B.PURCHS_PROGRS_STTUS, B.INVOICE_NUMBER, A.SETLE_ID, C.BYER_TEL_NO, C.BYER_EMAIL, A.RCPMNY_AMT, A.STORE_DELNG_ID, A.SETLE_ID, A.RCPMNY_NM, GET_CODE_NM('WOD009',A.CASH_RECEIPTS_OPT) AS CASH_RECEIPTS_OPT_NM from TB_WCRF_PAY A, TB_WCRF_BUY_TOT B, TB_WCRF_BUY_ADDR C where 0=0
AND A.STORE_DELNG_ID = #{storeDelngId}
AND A.PURCHS_LST_ID = #{purchsLstId}
AND A.PURCHS_LST_ID = B.PURCHS_LST_ID AND B.PURCHS_LST_ID = C.PURCHS_LST_ID order by A.RGST_DT desc limit 1

mapper.update:
UPDATE TB_WCRF_BUY_TOT SET
        PURCHS_PROGRS_STTUS= #{purchsProgrsSttus}
,       UPDTR_ID = #{updtrId}
,       UPDT_DT = now()

    ,       INVOICE_NUMBER= #{invoiceNumber}

WHERE   PURCHS_LST_ID = #{purchsLstId}

UPDATE TB_WCRF_PAY SET
        SETLE_DT = now()
,       CARD_CONFM_NO = #{cardConfmNo}
,       SETLE_AMT= #{setleAmt}
,       UPDTR_ID = #{updtrId}
,       UPDT_DT = now()

    ,       RCPMNY_AMT= #{rcpmnyAmt}

WHERE   STORE_DELNG_ID = #{storeDelngId}
